import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  const [enteredTtitle, setEnteredTitle] = useState('');
  const [enteredAmount,setEnteredAmount] = useState('');
  const [enteredDate,setEnteredDate] = useState('');

  /*>we can use one state istead of many for different const< 
    const [userInput, setUserInput] = useState({
    enteredTtitle: "",
    enteredAmount: "",
    enteredAmount: "",
  });

  */


  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
    console.log(enteredTtitle);
    /*
       // setUserInput({
      //   ...userInput,
      //   enteredTtitle : event.target.value,
      // })
    */

      // setUserInput((prevState) =>{
      //   return {...prevState,enteredTtitle: event.target.value}
     //this is the safer way to access the all current 
     //value updates of previous variables
      // })
  };
  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
    console.log(enteredAmount);

    /*
    setUserInput({
      ...userInput,
      enteredAmount: event.target.value,
    });
    */

  };
  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
    console.log(enteredDate);

    /*  setUserInput({
      ...userInput,
      enteredDate: event.target.value,
    });
    */

  };

  const submitHandler = (event) =>{
    event.preventDefault()

    const expenseData = {
      title: enteredTtitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    }
    setEnteredTitle
    setEnteredTitle('')
    setEnteredAmount('')
    console.log(expenseData);
  }
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            steps="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
        <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
        </div>
      </div>
    </form>
  );
};

export default ExpenseForm;
